import React, { Component } from "react";
import Jumbotron from "../components/Jumbotron";
import { BookListItem } from "../components/BookList";
import API from "../utils/API";
import { Container, Row, Col } from "../components/Grid";
import { Input, FormBtn } from "../components/Form";
// import "./Search.css";

class Books extends Component {
  state = {
    books: [],
    search: "",
    title: "",
    author: "",
    description: "",
  };

  componentDidMount() {}

  handleInputChange = (event) => {
    const { name, value } = event.target;
    this.setState({
      [name]: value,
    });
  };

  handleFormSubmit = (event) => {
    event.preventDefault();
    API.searchBook(this.state.search)
      .then((res) => this.setState({ books: res.data.items }))
      .catch((err) => console.log(err));
  };

  handleSavedBook = (data) => {
    console.log("This is the book data from handleSavedBook:");
    console.log(data);
    API.saveBook(data)
      .then((res) => alert("Your book was saved! ðŸ˜„") && this.loadBooks())

      .catch((err) => console.log(err));
  };

  render() {
    return (
      <div>
        <Jumbotron>
          {/* <h1>1</h1>
          <br></br>
          <br></br> */}
          <h1 className="titleHead"> React Google Books</h1>
          <h4>Find and Save Books for your Reading List!</h4>
        </Jumbotron>
        <Container fluid>
          <Row>
            <Col size="md-12">
              <div className="searchText">
                <form>
                  <div className="bookSearch ">
                    <h4>Book Search:</h4>
                  </div>

                  <Input
                    value={this.state.search}
                    onChange={this.handleInputChange}
                    name="search"
                    placeholder="Search for a Book"
                  />
                  <FormBtn
                    disabled={!this.state.search}
                    onClick={this.handleFormSubmit}
                  >
                    Search Book
                  </FormBtn>
                </form>
              </div>
            </Col>
          </Row>
          <Row>
            <Col size="md-12">
              {!this.state.books.length ? (
                <h3 id="message" className="text-center">
                  Currently No Books To Display
                </h3>
              ) : (
                <div>
                  {this.state.books.map((books) => {
                    return (
                      <BookListItem
                        key={books.id}
                        title={books.volumeInfo.title}
                        author={books.volumeInfo.authors}
                        id={books.id}
                        href={books.volumeInfo.previewLink}
                        thumbnail={books.volumeInfo.imageLinks.thumbnail}
                        description={books.volumeInfo.description}
                        handleSavedBook={() =>
                          this.handleSavedBook({
                            title: books.volumeInfo.title,
                            author: books.volumeInfo.authors,
                            id: books.id,
                            href: books.volumeInfo.previewLink,
                            thumbnail: books.volumeInfo.imageLinks.thumbnail,
                            description: books.volumeInfo.description,
                          })
                        }
                      />
                    );
                  })}
                </div>
              )}
            </Col>
          </Row>
        </Container>
      </div>
    );
  }
}

export default Books;
