import axios from "axios";
// const googleApiKey = process.env.API_KEY;
const myApiKey = "AIzaSyC9Tw6Y-gcvz_J5jQHOSe5gcd31M_QWC-s";

const API = {
  // Gets books from the Google API
  getBooks: function (q) {
    return axios.get("/api/google", { params: { q: "title:" + q + myApiKey } });
  },
  // Gets all saved books
  getSavedBooks: function () {
    return axios.get("/api/books");
  },
  // Deletes the saved book with the given id
  deleteBook: function (id) {
    return axios.delete("/api/books/" + id);
  },
  // Saves an book to the database
  saveBook: function (bookData) {
    return axios.post("/api/books", bookData);
  },
  searchBook: function (bookData) {
    console.log(bookData);
    return axios.get(
      `https://www.googleapis.com/books/v1/volumes?q=${bookData}&key=${myApiKey}`
    );
  },
};

export default API;
